select * from data_arrivals da LEFT JOIN product_types pt ON da.product_type_code = pt.`code` WHERE product_category_code = 'qi-xiang-liu-yao-su' AND product_type_code in ('ground_temperature','humidity','rainfall','wind','temperature','pressure') AND routine_time BETWEEN (SELECT date_sub((SELECT MAX(routine_time) from data_arrivals),interval 1 hour)) and (SELECT MAX(routine_time) from data_arrivals) ORDER BY routine_time;
